    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        String elementName = txtElementName.getText().trim();

    ElementDelete modifier = new ElementDelete();
    int rowsDeleted = modifier.deleteElement(elementName);

    if (rowsDeleted > 0) {
        JOptionPane.showMessageDialog(this, "Element deleted successfully.");
    } else {
        JOptionPane.showMessageDialog(this, "Element not found or delete failed.");
    } 
    }//GEN-LAST:event_btnDeleteActionPerformed

